--基本信息
create table baseinfo{
    --表的序列
    b_id int(10) not null,
    --学生学号
    b_num int(10) not null,
    --学生姓名
    b_name varchar(255) not null,
    --性别
    b_sex varchar(10) not null,
    --学生生日
    b_birth date not null;
    --学院编号
    b_college int(10) not null,
    --学生班级
    b_class int(5),
    --联系方式
    b_phone int(11)
}ENGINE=InnoDB CHARSET=utf8;

SELECT * FROM baseinfo;

